import {useEffect, useState} from "react";
import {BaseResponse, UpdateInfoRequest} from "../interfaces";

export function CheckName() {
    const [name, setName] = useState("");
    const [status, setStatus] = useState("INITIAL" as
        | "INITIAL"
        | "SEND_DATA"
        | "SENDING_DATA"
        | "DATA_SENDED"
        | "ERROR_SENDING_DATA"
    );
    const [result, setResult] = useState<BaseResponse | null>(null);

    useEffect(() => {
        if (status === "SEND_DATA") {
            setStatus("SENDING_DATA");

            fetch("http://localhost:3001/info/validate", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({name} as UpdateInfoRequest),
            })
                .then((r) => r.json())
                .then((json) => {
                    setResult(json);
                    if (json.success) setStatus("DATA_SENDED");
                    else setStatus("ERROR_SENDING_DATA");
                });
        }
    }, [status, name]);

    return (
        <div className="page-container">
            <div className="form-card">
                <h1 className="form-title">Check Name</h1>

                <div className="form-field">
                    <label className="form-label">Name</label>
                    <input
                        className="form-input"
                        value={name}
                        placeholder="Enter a name..."
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>

                <button className="form-button" onClick={() => setStatus("SEND_DATA")}>
                    Validate Name
                </button>
            </div>

            {result && (
                <div className="result-card">
                    <h3 className="result-title">
                        {result.success ? "Valid Name" : "Validation Error"}
                    </h3>

                    <pre className="result-pre">
            {JSON.stringify(result, null, 2)}
          </pre>
                </div>
            )}
        </div>
    );
}
